mapper.select:
select articleno, userid, (select username from member m where a.userid = m.userid) as username, subject, content, hit, case when date_format(regtime, '%Y%m%d') = date_format(now(), '%Y%m%d') then date_format(regtime, '%H:%i:%s') else date_format(regtime, '%y.%m.%d') end regtime from article a
where ${key} like concat('%', #{word}, '%')
order by articleno desc limit #{start}, #{spp}

select count(articleno) from article
where subject like concat('%', #{word}, '%')
where ${key} = #{word}

select articleno, (select username from member m where a.userid = m.userid) as username, userid, subject, content, hit, regtime
from article a
where articleno = #{articleno}

mapper.update:
update article
set hit = hit + 1
where articleno = #{articleno}

update article
set subject = #{subject}, content = #{content}
where articleno = #{articleno}
